import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Stack,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Slider,
  Button,
  Collapse,
  IconButton,
  Tabs,
  Tab,
  useMediaQuery
} from '@mui/material';
import {
  Pets as PetsIcon,
  Straighten as SizeIcon,
  Palette as ColorIcon,
  Info as InfoIcon,
  RestartAlt as ResetIcon,
  ExpandMore as ExpandMoreIcon,
  Visibility as BinocularsIcon,
  FilterList as FilterIcon
} from '@mui/icons-material';
import { styled } from '@mui/material/styles';
import animalsData from './data/animals.json';
import { alpha } from '@mui/material/styles';

const ExpandMore = styled((props) => {
  const { expand, ...other } = props;
  return <IconButton {...other} />;
})(({ theme, expand }) => ({
  transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
  marginLeft: 'auto',
  transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
  }),
}));

const StyledCard = styled(Card)(({ theme }) => ({
  position: 'relative',
  overflow: 'hidden',
  '& .MuiCardMedia-root': {
    height: 0,
    paddingTop: '100%',
    transition: 'all 0.3s ease-in-out',
  },
  '& .MuiCardContent-root': {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    background: 'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 50%, rgba(0,0,0,0) 100%)',
    color: 'white',
    padding: theme.spacing(2),
    transition: 'all 0.3s ease-in-out',
    transform: 'translateY(0)',
    '&.expanded': {
      background: 'white',
      color: theme.palette.text.primary,
      position: 'relative',
      transform: 'translateY(0)'
    }
  }
}));

const InfoLabel = styled(Box)(({ theme }) => ({
  position: 'relative',
  backgroundColor: 'rgba(0, 0, 0, 0.6)',
  color: 'white',
  padding: '2px 8px',
  borderRadius: '12px',
  fontSize: '0.75rem',
  display: 'inline-flex',
  alignItems: 'center',
  gap: '4px',
  backdropFilter: 'blur(2px)',
  zIndex: 2,
  width: 'auto',
  alignSelf: 'flex-end'
}));

const FilterContainer = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  marginBottom: theme.spacing(4),
  borderRadius: theme.spacing(2),
  '& .MuiDivider-root': {
    margin: theme.spacing(3, 0)
  }
}));

const FilterSection = ({ title, category, values, selectedValues, onValueClick }) => (
  <Box>
    <Typography 
      variant="subtitle1" 
      sx={{ 
        mb: 2,
        fontWeight: 600,
        color: 'text.primary'
      }}
    >
      {title}
    </Typography>
    <Stack 
      direction="row" 
      spacing={1} 
      useFlexGap 
      flexWrap="wrap"
      sx={{
        '& .MuiChip-root': {
          borderRadius: '8px',
          transition: 'all 0.2s ease-in-out',
          '&:hover': {
            transform: 'translateY(-1px)',
            boxShadow: 1
          }
        }
      }}
    >
      {values.map(value => (
        <Chip
          key={value}
          label={value}
          onClick={() => onValueClick(category, value)}
          color={selectedValues.includes(value) ? "primary" : "default"}
          variant={selectedValues.includes(value) ? "filled" : "outlined"}
          sx={{ mb: 1 }}
        />
      ))}
    </Stack>
  </Box>
);

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#2e7d32',
    },
    secondary: {
      main: '#ff9800',
    },
  },
  typography: {
    fontFamily: '"Open Sans", sans-serif',
    h1: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h2: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h3: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h4: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h5: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h6: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    subtitle1: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
    },
    subtitle2: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
    },
    body1: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 400,
    },
    body2: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 400,
    },
    caption: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 300,
    },
    button: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
      textTransform: 'none',
    },
  },
});

function App() {
  const minSize = Math.floor(Math.min(...animalsData.animals.map(animal => animal.size)) * 0.8);
  const maxSize = Math.ceil(Math.max(...animalsData.animals.map(animal => animal.size)) * 1.2);

  const [animals, setAnimals] = useState(animalsData.animals);
  const [filters, setFilters] = useState({
    type: [],
    color: []
  });
  const [sizeRange, setSizeRange] = useState([minSize, maxSize]);
  const [expandedCards, setExpandedCards] = useState({});
  const [spottedAnimals, setSpottedAnimals] = useState(() => {
    const saved = localStorage.getItem('spottedAnimals');
    return saved ? JSON.parse(saved) : {};
  });
  const [activeTab, setActiveTab] = useState(0);
  const [isFilterExpanded, setIsFilterExpanded] = useState(false);
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  useEffect(() => {
    localStorage.setItem('spottedAnimals', JSON.stringify(spottedAnimals));
  }, [spottedAnimals]);

  const handleSpottedToggle = (id) => {
    setSpottedAnimals(prev => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  const uniqueValues = (key) => {
    const values = new Set();
    animalsData.animals.forEach(animal => {
      if (Array.isArray(animal[key])) {
        animal[key].forEach(value => values.add(value));
      } else {
        values.add(animal[key]);
      }
    });
    return Array.from(values).sort();
  };

  const handleFilterClick = (category, value) => {
    setFilters(prev => {
      const currentValues = prev[category];
      const newValues = currentValues.includes(value)
        ? currentValues.filter(v => v !== value)
        : [...currentValues, value];

      return {
        ...prev,
        [category]: newValues
      };
    });
  };

  const handleSizeChange = (event, newValue) => {
    setSizeRange(newValue);
  };

  const handleExpandClick = (id) => {
    setExpandedCards(prev => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  useEffect(() => {
    const filteredAnimals = animalsData.animals.filter(animal => {
      // First filter by spotted status if on spotted tab
      if (activeTab === 1 && !spottedAnimals[animal.id]) {
        return false;
      }

      // Then apply other filters
      const typeColorMatch = Object.entries(filters).every(([key, values]) => {
        if (values.length === 0) return true;
        if (Array.isArray(animal[key])) {
          return values.some(value => animal[key].includes(value));
        }
        return values.includes(animal[key]);
      });

      const sizeMargin = animal.size * 0.2;
      const minSizeWithMargin = animal.size - sizeMargin;
      const maxSizeWithMargin = animal.size + sizeMargin;

      const sizeMatch = 
        (minSizeWithMargin <= sizeRange[1]) && 
        (maxSizeWithMargin >= sizeRange[0]);

      return typeColorMatch && sizeMatch;
    });
    setAnimals(filteredAnimals);
  }, [filters, sizeRange, activeTab, spottedAnimals]);

  const handleImageError = (e) => {
    e.target.src = '/images/placeholder.jpg';
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const resetFilters = () => {
    setFilters({
      type: [],
      color: []
    });
    setSizeRange([minSize, maxSize]);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h3" component="h1" gutterBottom align="center">
            Madagaskar Wildlife Guide
          </Typography>

          <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
            <Tabs 
              value={activeTab} 
              onChange={handleTabChange} 
              variant="fullWidth"
              textColor="primary"
              indicatorColor="primary"
            >
              <Tab 
                label={
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <span>Alle dieren</span>
                    <Chip 
                      label={animalsData.animals.length} 
                      size="small" 
                      color={activeTab === 0 ? "primary" : "default"}
                    />
                  </Box>
                } 
              />
              <Tab 
                label={
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <span>Gespot</span>
                    <Chip 
                      label={Object.values(spottedAnimals).filter(Boolean).length} 
                      size="small"
                      color={activeTab === 1 ? "primary" : "default"}
                    />
                  </Box>
                } 
              />
            </Tabs>
          </Box>

          <Paper 
            sx={{ 
              mb: 3,
              overflow: 'hidden',
              transition: 'all 0.3s ease-in-out'
            }}
            elevation={0}
          >
            <Box
              sx={{
                p: 2,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                borderBottom: isMobile && !isFilterExpanded ? 'none' : '1px solid',
                borderColor: 'divider'
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                <FilterIcon color="action" />
                <Typography variant="subtitle1" sx={{ fontWeight: 500 }}>
                  Filters
                </Typography>
                {isMobile && (
                  <Typography variant="caption" color="text.secondary" sx={{ ml: 1 }}>
                    {filters.type.length + filters.color.length > 0 ? 
                      `${filters.type.length + filters.color.length} actief` : 
                      'Geen filters actief'}
                  </Typography>
                )}
              </Box>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                <Button
                  variant="outlined"
                  startIcon={<ResetIcon />}
                  onClick={resetFilters}
                  color="secondary"
                  size="small"
                  sx={{
                    borderRadius: '8px',
                    textTransform: 'none',
                    '&:hover': {
                      transform: 'translateY(-1px)',
                      boxShadow: 1
                    }
                  }}
                >
                  Reset
                </Button>
                {isMobile && (
                  <IconButton 
                    size="small" 
                    onClick={() => setIsFilterExpanded(!isFilterExpanded)}
                    sx={{ ml: 1 }}
                  >
                    <ExpandMoreIcon 
                      sx={{ 
                        transform: isFilterExpanded ? 'rotate(180deg)' : 'rotate(0)',
                        transition: 'transform 0.3s ease-in-out'
                      }} 
                    />
                  </IconButton>
                )}
              </Box>
            </Box>

            <Collapse in={!isMobile || isFilterExpanded}>
              <Box sx={{ p: 2 }}>
                <FilterSection
                  title="Type"
                  category="type"
                  values={uniqueValues('type')}
                  selectedValues={filters.type}
                  onValueClick={handleFilterClick}
                />

                <Divider sx={{ my: 2 }} />

                <FilterSection
                  title="Kleur"
                  category="color"
                  values={uniqueValues('color')}
                  selectedValues={filters.color}
                  onValueClick={handleFilterClick}
                />

                <Divider sx={{ my: 2 }} />

                <Box>
                  <Typography 
                    variant="subtitle1" 
                    sx={{ 
                      mb: 2,
                      fontWeight: 600,
                      color: 'text.primary'
                    }}
                  >
                    Grootte (cm)
                  </Typography>
                  <Box sx={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: 2,
                    px: 2,
                    mt: 1
                  }}>
                    <Typography 
                      variant="caption" 
                      sx={{ 
                        color: 'text.secondary',
                        minWidth: '24px',
                        textAlign: 'right'
                      }}
                    >
                      {sizeRange[0]}
                    </Typography>
                    <Slider
                      value={sizeRange}
                      onChange={handleSizeChange}
                      valueLabelDisplay="auto"
                      min={minSize}
                      max={maxSize}
                      sx={{ 
                        '& .MuiSlider-rail': {
                          opacity: 0.32
                        },
                        '& .MuiSlider-track': {
                          border: 'none'
                        },
                        '& .MuiSlider-thumb': {
                          width: 12,
                          height: 12,
                          '&:hover, &.Mui-focusVisible': {
                            boxShadow: `0 0 0 8px ${alpha(theme.palette.primary.main, 0.16)}`
                          },
                          '&.Mui-active': {
                            boxShadow: `0 0 0 12px ${alpha(theme.palette.primary.main, 0.16)}`
                          }
                        },
                        flexGrow: 1
                      }}
                    />
                    <Typography 
                      variant="caption" 
                      sx={{ 
                        color: 'text.secondary',
                        minWidth: '24px'
                      }}
                    >
                      {sizeRange[1]}
                    </Typography>
                  </Box>
                  <Typography 
                    variant="caption" 
                    color="text.secondary" 
                    sx={{ 
                      mt: 2,
                      display: 'block',
                      textAlign: 'center',
                      fontStyle: 'italic',
                      opacity: 0.7
                    }}
                  >
                    Let op: er wordt een foutmarge van 20% toegepast bij het filteren
                  </Typography>
                </Box>
              </Box>
            </Collapse>
          </Paper>

          {animals.length === 0 ? (
            <Box sx={{ textAlign: 'center', py: 4 }}>
              <Typography variant="h6" color="text.secondary">
                {activeTab === 1 ? 
                  "Je hebt nog geen dieren gespot die aan de filters voldoen." :
                  "Geen dieren gevonden die aan de filters voldoen."
                }
              </Typography>
            </Box>
          ) : (
            <Grid container spacing={2}>
              {animals.map(animal => (
                <Grid item xs={6} sm={4} md={3} key={animal.id}>
                  <StyledCard>
                    <CardMedia
                      component="img"
                      image={animal.image}
                      alt={animal.name}
                      onError={handleImageError}
                      sx={{
                        objectFit: 'cover',
                        backgroundColor: 'grey.100',
                        filter: spottedAnimals[animal.id] ? 'none' : 'grayscale(0.5)'
                      }}
                    />
                    <Box sx={{ 
                      position: 'absolute', 
                      top: theme.spacing(1), 
                      left: theme.spacing(1),
                      zIndex: 3
                    }}>
                      <IconButton
                        size="small"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleSpottedToggle(animal.id);
                        }}
                        sx={{ 
                          color: 'white',
                          backgroundColor: spottedAnimals[animal.id] ? 'primary.main' : 'rgba(0, 0, 0, 0.2)',
                          backdropFilter: 'blur(2px)',
                          transition: 'all 0.2s ease-in-out',
                          '&:hover': {
                            backgroundColor: spottedAnimals[animal.id] ? 'primary.dark' : 'rgba(0, 0, 0, 0.3)',
                            transform: 'scale(1.05)'
                          }
                        }}
                      >
                        <BinocularsIcon 
                          fontSize="small"
                          sx={{
                            transform: 'translateY(-1px)'
                          }}
                        />
                      </IconButton>
                    </Box>
                    <Box sx={{ 
                      position: 'absolute', 
                      top: theme.spacing(1), 
                      right: theme.spacing(1),
                      display: 'flex',
                      flexDirection: 'column',
                      gap: theme.spacing(0.5),
                      zIndex: 3,
                      alignItems: 'flex-end'
                    }}>
                      <InfoLabel>
                        <PetsIcon sx={{ fontSize: '0.9rem' }} />
                        {animal.type}
                      </InfoLabel>
                      <InfoLabel>
                        <SizeIcon sx={{ fontSize: '0.9rem' }} />
                        {animal.size} cm
                      </InfoLabel>
                    </Box>
                    <CardContent className={expandedCards[animal.id] ? 'expanded' : ''}>
                      <Box sx={{ 
                        display: 'flex', 
                        alignItems: 'center', 
                        mb: 1,
                        position: 'relative',
                        zIndex: 1
                      }}>
                        <Box sx={{ flexGrow: 1 }}>
                          <Typography 
                            variant="h6" 
                            component="div" 
                            noWrap
                            sx={{
                              fontWeight: 600,
                              letterSpacing: '0.5px',
                              fontSize: '1rem'
                            }}
                          >
                            {animal.name}
                          </Typography>
                          <Typography 
                            variant="body2" 
                            noWrap
                            sx={{
                              fontFamily: '"Playfair Display", serif',
                              fontStyle: 'italic',
                              fontSize: '0.9rem',
                              color: expandedCards[animal.id] ? 'text.secondary' : 'rgba(255, 255, 255, 0.9)',
                              letterSpacing: '0.3px'
                            }}
                          >
                            {animal.scientificName}
                          </Typography>
                        </Box>
                        <ExpandMore
                          expand={expandedCards[animal.id]}
                          onClick={() => handleExpandClick(animal.id)}
                          aria-expanded={expandedCards[animal.id]}
                          aria-label="show more"
                          sx={{ color: expandedCards[animal.id] ? 'inherit' : 'white' }}
                        >
                          <ExpandMoreIcon />
                        </ExpandMore>
                      </Box>

                      <Box sx={{ 
                        position: 'relative',
                        zIndex: 0,
                        mt: 1
                      }}>
                        <Collapse 
                          in={expandedCards[animal.id]} 
                          timeout={300} 
                          unmountOnExit
                          sx={{
                            transition: 'all 0.3s ease-in-out',
                            '&.MuiCollapse-entered': {
                              height: 'auto',
                              minHeight: '100%'
                            }
                          }}
                        >
                          <List dense disablePadding>
                            <ListItem disablePadding>
                              <ListItemIcon sx={{ minWidth: 36 }}>
                                <PetsIcon fontSize="small" />
                              </ListItemIcon>
                              <ListItemText 
                                primary="Type" 
                                secondary={animal.type}
                                primaryTypographyProps={{ 
                                  variant: 'caption',
                                  sx: { 
                                    fontFamily: '"Montserrat", sans-serif',
                                    fontWeight: 500,
                                    letterSpacing: '0.5px'
                                  }
                                }}
                                secondaryTypographyProps={{ 
                                  variant: 'body2',
                                  sx: {
                                    letterSpacing: '0.3px'
                                  }
                                }}
                              />
                            </ListItem>
                            <ListItem disablePadding>
                              <ListItemIcon sx={{ minWidth: 36 }}>
                                <SizeIcon fontSize="small" />
                              </ListItemIcon>
                              <ListItemText 
                                primary="Grootte" 
                                secondary={`${animal.size} cm (ongeveer)`}
                                primaryTypographyProps={{ variant: 'caption' }}
                                secondaryTypographyProps={{ variant: 'body2' }}
                              />
                            </ListItem>
                            <ListItem disablePadding>
                              <ListItemIcon sx={{ minWidth: 36 }}>
                                <ColorIcon fontSize="small" />
                              </ListItemIcon>
                              <ListItemText 
                                primary="Kleuren" 
                                secondary={animal.color.join(', ')}
                                primaryTypographyProps={{ variant: 'caption' }}
                                secondaryTypographyProps={{ variant: 'body2' }}
                              />
                            </ListItem>
                            <ListItem disablePadding>
                              <ListItemIcon sx={{ minWidth: 36 }}>
                                <InfoIcon fontSize="small" />
                              </ListItemIcon>
                              <ListItemText 
                                primary="Dieet" 
                                secondary={animal.diet}
                                primaryTypographyProps={{ variant: 'caption' }}
                                secondaryTypographyProps={{ variant: 'body2' }}
                              />
                            </ListItem>
                          </List>

                          <Box sx={{ mt: 1 }}>
                            <Typography 
                              variant="caption" 
                              gutterBottom
                              sx={{
                                fontFamily: '"Montserrat", sans-serif',
                                fontWeight: 500,
                                letterSpacing: '0.5px',
                                color: 'text.secondary',
                                display: 'block'
                              }}
                            >
                              Uiterlijke kenmerken:
                            </Typography>
                            <Typography 
                              variant="body2" 
                              paragraph 
                              sx={{ 
                                fontSize: '0.8rem',
                                letterSpacing: '0.3px',
                                lineHeight: 1.6
                              }}
                            >
                              {animal.description}
                            </Typography>
                          </Box>

                          <Box sx={{ mt: 1 }}>
                            <Typography 
                              variant="caption" 
                              gutterBottom
                              sx={{
                                fontFamily: '"Montserrat", sans-serif',
                                fontWeight: 500,
                                letterSpacing: '0.5px',
                                color: 'text.secondary',
                                display: 'block'
                              }}
                            >
                              Habitat:
                            </Typography>
                            <Typography 
                              variant="body2" 
                              sx={{ 
                                fontSize: '0.8rem',
                                letterSpacing: '0.3px'
                              }}
                            >
                              {animal.habitat}
                            </Typography>
                          </Box>
                        </Collapse>
                      </Box>
                    </CardContent>
                  </StyledCard>
                </Grid>
              ))}
            </Grid>
          )}
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
