import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Stack,
  IconButton,
  Tabs,
  Tab
} from '@mui/material';
import {
  Pets as PetsIcon,
  Straighten as SizeIcon,
  Visibility as BinocularsIcon,
} from '@mui/icons-material';
import { styled } from '@mui/material/styles';
import animalsData from './data/animals.json';
import { alpha } from '@mui/material/styles';

const StyledCard = styled(Card)(({ theme }) => ({
  position: 'relative',
  overflow: 'hidden',
  '& .MuiCardMedia-root': {
    height: 0,
    paddingTop: '100%',
    transition: 'all 0.3s ease-in-out',
  },
  '& .MuiCardContent-root': {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    background: 'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 50%, rgba(0,0,0,0) 100%)',
    color: 'white',
    padding: 0,
    '&:last-child': {
      paddingBottom: 0
    }
  }
}));

const InfoLabel = styled(Box)(({ theme }) => ({
  position: 'relative',
  backgroundColor: 'rgba(0, 0, 0, 0.6)',
  color: 'white',
  padding: '1px 6px',
  borderRadius: '8px',
  fontSize: '0.675rem',
  display: 'inline-flex',
  alignItems: 'center',
  gap: '3px',
  backdropFilter: 'blur(2px)',
  zIndex: 2,
  width: 'auto',
  alignSelf: 'flex-end',
  '& .MuiSvgIcon-root': {
    fontSize: '0.75rem'
  }
}));

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#2e7d32',
    },
    secondary: {
      main: '#ff9800',
    },
  },
  typography: {
    fontFamily: '"Open Sans", sans-serif',
    h1: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h2: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h3: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h4: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h5: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h6: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    subtitle1: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
    },
    subtitle2: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
    },
    body1: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 400,
    },
    body2: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 400,
    },
    caption: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 300,
    },
    button: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
      textTransform: 'none',
    },
  },
});

function App() {
  const [animals, setAnimals] = useState(animalsData.animals);
  const [spottedAnimals, setSpottedAnimals] = useState(() => {
    const saved = localStorage.getItem('spottedAnimals');
    return saved ? JSON.parse(saved) : {};
  });
  const [activeTab, setActiveTab] = useState(0);

  useEffect(() => {
    localStorage.setItem('spottedAnimals', JSON.stringify(spottedAnimals));
  }, [spottedAnimals]);

  const handleSpottedToggle = (id) => {
    setSpottedAnimals(prev => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  const handleImageError = (e) => {
    e.target.src = '/images/placeholder.jpg';
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  useEffect(() => {
    const filteredAnimals = animalsData.animals.filter(animal => {
      if (activeTab === 1 && !spottedAnimals[animal.id]) {
        return false;
      }
      return true;
    });
    setAnimals(filteredAnimals);
  }, [activeTab, spottedAnimals]);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h3" component="h1" gutterBottom align="center">
            Madagaskar Wildlife Guide
          </Typography>

          <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
            <Tabs 
              value={activeTab} 
              onChange={handleTabChange} 
              variant="fullWidth"
              textColor="primary"
              indicatorColor="primary"
            >
              <Tab 
                label={
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <span>Alle dieren</span>
                    <Chip 
                      label={animalsData.animals.length} 
                      size="small" 
                      color={activeTab === 0 ? "primary" : "default"}
                    />
                  </Box>
                } 
              />
              <Tab 
                label={
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <span>Gespot</span>
                    <Chip 
                      label={Object.values(spottedAnimals).filter(Boolean).length} 
                      size="small"
                      color={activeTab === 1 ? "primary" : "default"}
                    />
                  </Box>
                } 
              />
            </Tabs>
          </Box>

          {animals.length === 0 ? (
            <Box sx={{ textAlign: 'center', py: 4 }}>
              <Typography variant="h6" color="text.secondary">
                {activeTab === 1 ? 
                  "Je hebt nog geen dieren gespot die aan de filters voldoen." :
                  "Geen dieren gevonden die aan de filters voldoen."
                }
              </Typography>
            </Box>
          ) : (
            <Grid container spacing={2}>
              {animals.map(animal => (
                <Grid item xs={6} sm={4} md={3} key={animal.id}>
                  <StyledCard>
                    <CardMedia
                      component="img"
                      image={animal.image}
                      alt={animal.name}
                      onError={handleImageError}
                      sx={{
                        objectFit: 'cover',
                        backgroundColor: 'grey.100',
                        filter: spottedAnimals[animal.id] ? 'none' : 'grayscale(0.5)'
                      }}
                    />
                    <Box sx={{ 
                      position: 'absolute', 
                      top: theme.spacing(1), 
                      left: theme.spacing(1),
                      zIndex: 3
                    }}>
                      <IconButton
                        size="small"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleSpottedToggle(animal.id);
                        }}
                        sx={{ 
                          color: 'white',
                          backgroundColor: spottedAnimals[animal.id] ? 'primary.main' : 'rgba(0, 0, 0, 0.2)',
                          backdropFilter: 'blur(2px)',
                          transition: 'all 0.2s ease-in-out',
                          '&:hover': {
                            backgroundColor: spottedAnimals[animal.id] ? 'primary.dark' : 'rgba(0, 0, 0, 0.3)',
                            transform: 'scale(1.05)'
                          }
                        }}
                      >
                        <BinocularsIcon 
                          fontSize="small"
                          sx={{
                            transform: 'translateY(-1px)'
                          }}
                        />
                      </IconButton>
                    </Box>
                    <Box sx={{ 
                      position: 'absolute', 
                      top: theme.spacing(1), 
                      right: theme.spacing(1),
                      display: 'flex',
                      flexDirection: 'column',
                      gap: 0.5,
                      zIndex: 3,
                      alignItems: 'flex-end'
                    }}>
                      <InfoLabel>
                        <PetsIcon />
                        {animal.type}
                      </InfoLabel>
                      <InfoLabel>
                        <SizeIcon />
                        {animal.size} cm
                      </InfoLabel>
                    </Box>
                    <CardContent>
                      <Box sx={{ 
                        display: 'flex', 
                        flexDirection: 'column',
                        position: 'relative',
                        minHeight: '100%',
                        px: 1
                      }}>
                        <Box sx={{ 
                          display: 'flex', 
                          alignItems: 'flex-end',
                          width: '100%',
                          mb: 0.5,
                          position: 'relative',
                          zIndex: 1,
                          mt: 'auto'
                        }}>
                          <Box sx={{ flexGrow: 1 }}>
                            <Typography 
                              variant="h6" 
                              component="div" 
                              noWrap
                              sx={{
                                fontWeight: 600,
                                letterSpacing: '0.5px',
                                fontSize: '0.9rem',
                                mb: 0.25,
                                lineHeight: 1.2,
                                textShadow: '0 1px 2px rgba(0,0,0,0.5)'
                              }}
                            >
                              {animal.name}
                            </Typography>
                            <Typography 
                              variant="body2" 
                              noWrap
                              sx={{
                                fontFamily: '"Playfair Display", serif',
                                fontStyle: 'italic',
                                fontSize: '0.75rem',
                                color: 'rgba(255, 255, 255, 0.9)',
                                letterSpacing: '0.3px',
                                lineHeight: 1.2,
                                textShadow: '0 1px 2px rgba(0,0,0,0.5)'
                              }}
                            >
                              {animal.scientificName}
                            </Typography>
                          </Box>
                        </Box>
                      </Box>
                    </CardContent>
                  </StyledCard>
                </Grid>
              ))}
            </Grid>
          )}
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
