import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Stack,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Slider,
  Input,
  Button,
  Collapse,
  IconButton
} from '@mui/material';
import {
  Pets as PetsIcon,
  Straighten as SizeIcon,
  Palette as ColorIcon,
  Info as InfoIcon,
  RestartAlt as ResetIcon,
  ExpandMore as ExpandMoreIcon
} from '@mui/icons-material';
import { styled } from '@mui/material/styles';
import animalsData from './data/animals.json';

const ExpandMore = styled((props) => {
  const { expand, ...other } = props;
  return <IconButton {...other} />;
})(({ theme, expand }) => ({
  transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
  marginLeft: 'auto',
  transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
  }),
}));

const StyledCard = styled(Card)(({ theme }) => ({
  position: 'relative',
  overflow: 'hidden',
  '& .MuiCardMedia-root': {
    height: 0,
    paddingTop: '100%',
    transition: 'all 0.3s ease-in-out',
  },
  '& .MuiCardContent-root': {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    background: 'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 50%, rgba(0,0,0,0) 100%)',
    color: 'white',
    padding: theme.spacing(2),
    transition: 'all 0.3s ease-in-out',
    transform: 'translateY(0)',
    '&.expanded': {
      background: 'white',
      color: theme.palette.text.primary,
      position: 'relative',
      transform: 'translateY(0)'
    }
  }
}));

const InfoLabel = styled(Box)(({ theme }) => ({
  position: 'relative',
  backgroundColor: 'rgba(0, 0, 0, 0.6)',
  color: 'white',
  padding: '2px 8px',
  borderRadius: '12px',
  fontSize: '0.75rem',
  display: 'flex',
  alignItems: 'center',
  gap: '4px',
  backdropFilter: 'blur(2px)',
  zIndex: 2,
  minWidth: 'fit-content'
}));

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#2e7d32',
    },
    secondary: {
      main: '#ff9800',
    },
  },
  typography: {
    fontFamily: '"Open Sans", sans-serif',
    h1: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h2: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h3: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h4: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h5: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    h6: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
    subtitle1: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
    },
    subtitle2: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
    },
    body1: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 400,
    },
    body2: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 400,
    },
    caption: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 300,
    },
    button: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
      textTransform: 'none',
    },
  },
});

function App() {
  const [animals, setAnimals] = useState(animalsData.animals);
  const [filters, setFilters] = useState({
    type: [],
    color: []
  });
  const [sizeRange, setSizeRange] = useState([0, 100]);
  const [expandedCards, setExpandedCards] = useState({});

  const uniqueValues = (key) => {
    const values = new Set();
    animalsData.animals.forEach(animal => {
      if (Array.isArray(animal[key])) {
        animal[key].forEach(value => values.add(value));
      } else {
        values.add(animal[key]);
      }
    });
    return Array.from(values).sort();
  };

  const handleFilterClick = (category, value) => {
    setFilters(prev => {
      const currentValues = prev[category];
      const newValues = currentValues.includes(value)
        ? currentValues.filter(v => v !== value)
        : [...currentValues, value];

      return {
        ...prev,
        [category]: newValues
      };
    });
  };

  const handleSizeChange = (event, newValue) => {
    setSizeRange(newValue);
  };

  const handleSizeInputChange = (index) => (event) => {
    const value = event.target.value === '' ? 0 : Number(event.target.value);
    const newRange = [...sizeRange];
    newRange[index] = value;
    setSizeRange(newRange);
  };

  const resetFilters = () => {
    setFilters({
      type: [],
      color: []
    });
    setSizeRange([0, 100]);
  };

  const handleExpandClick = (id) => {
    setExpandedCards(prev => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  useEffect(() => {
    const filteredAnimals = animalsData.animals.filter(animal => {
      // Check type and color filters
      const typeColorMatch = Object.entries(filters).every(([key, values]) => {
        if (values.length === 0) return true;
        if (Array.isArray(animal[key])) {
          return values.some(value => animal[key].includes(value));
        }
        return values.includes(animal[key]);
      });

      // Check size range with 20% margin
      const sizeMargin = animal.size * 0.2;
      const minSize = sizeRange[0];
      const maxSize = sizeRange[1];
      const sizeMatch = animal.size >= (minSize - sizeMargin) && 
                       animal.size <= (maxSize + sizeMargin);

      return typeColorMatch && sizeMatch;
    });
    setAnimals(filteredAnimals);
  }, [filters, sizeRange]);

  const handleImageError = (e) => {
    e.target.src = '/images/placeholder.jpg';
  };

  const FilterSection = ({ title, category, values }) => (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle1" sx={{ mb: 1, fontWeight: 'bold' }}>
        {title}
      </Typography>
      <Stack direction="row" spacing={1} useFlexGap flexWrap="wrap">
        {values.map(value => (
          <Chip
            key={value}
            label={value}
            onClick={() => handleFilterClick(category, value)}
            color={filters[category].includes(value) ? "primary" : "default"}
            variant={filters[category].includes(value) ? "filled" : "outlined"}
            sx={{ mb: 1 }}
          />
        ))}
      </Stack>
    </Box>
  );

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h3" component="h1" gutterBottom align="center">
            Madagaskar Wildlife Guide
          </Typography>

          <Paper sx={{ p: 3, mb: 4 }}>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 2 }}>
              <Button
                variant="outlined"
                startIcon={<ResetIcon />}
                onClick={resetFilters}
                color="secondary"
              >
                Reset Filters
              </Button>
            </Box>

            <FilterSection
              title="Type"
              category="type"
              values={uniqueValues('type')}
            />
            <Divider sx={{ my: 2 }} />
            <FilterSection
              title="Kleur"
              category="color"
              values={uniqueValues('color')}
            />
            <Divider sx={{ my: 2 }} />
            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle1" sx={{ mb: 1, fontWeight: 'bold' }}>
                Grootte (cm)
              </Typography>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <Input
                  value={sizeRange[0]}
                  onChange={handleSizeInputChange(0)}
                  inputProps={{
                    min: 0,
                    max: 100,
                    type: 'number',
                  }}
                  sx={{ width: 60 }}
                />
                <Slider
                  value={sizeRange}
                  onChange={handleSizeChange}
                  valueLabelDisplay="auto"
                  min={0}
                  max={100}
                  sx={{ flexGrow: 1 }}
                />
                <Input
                  value={sizeRange[1]}
                  onChange={handleSizeInputChange(1)}
                  inputProps={{
                    min: 0,
                    max: 100,
                    type: 'number',
                  }}
                  sx={{ width: 60 }}
                />
              </Box>
              <Typography variant="caption" color="text.secondary" sx={{ mt: 1, display: 'block' }}>
                Let op: er wordt een foutmarge van 20% toegepast bij het filteren
              </Typography>
            </Box>
          </Paper>

          <Grid container spacing={2}>
            {animals.map(animal => (
              <Grid item xs={6} sm={4} md={3} key={animal.id}>
                <StyledCard>
                  <CardMedia
                    component="img"
                    image={animal.image}
                    alt={animal.name}
                    onError={handleImageError}
                    sx={{
                      objectFit: 'cover',
                      backgroundColor: 'grey.100'
                    }}
                  />
                  <Box sx={{ 
                    position: 'absolute', 
                    top: theme.spacing(1), 
                    right: theme.spacing(1),
                    display: 'flex',
                    flexDirection: 'column',
                    gap: theme.spacing(0.5),
                    zIndex: 3
                  }}>
                    <InfoLabel>
                      <PetsIcon sx={{ fontSize: '0.9rem' }} />
                      {animal.type}
                    </InfoLabel>
                    <InfoLabel>
                      <SizeIcon sx={{ fontSize: '0.9rem' }} />
                      {animal.size} cm
                    </InfoLabel>
                  </Box>
                  <CardContent className={expandedCards[animal.id] ? 'expanded' : ''}>
                    <Box sx={{ 
                      display: 'flex', 
                      alignItems: 'center', 
                      mb: 1,
                      position: 'relative',
                      zIndex: 1
                    }}>
                      <Box sx={{ flexGrow: 1 }}>
                        <Typography 
                          variant="h6" 
                          component="div" 
                          noWrap
                          sx={{
                            fontWeight: 600,
                            letterSpacing: '0.5px',
                            fontSize: '1rem'
                          }}
                        >
                          {animal.name}
                        </Typography>
                        <Typography 
                          variant="body2" 
                          noWrap
                          sx={{
                            fontFamily: '"Playfair Display", serif',
                            fontStyle: 'italic',
                            fontSize: '0.9rem',
                            color: expandedCards[animal.id] ? 'text.secondary' : 'rgba(255, 255, 255, 0.9)',
                            letterSpacing: '0.3px'
                          }}
                        >
                          {animal.scientificName}
                        </Typography>
                      </Box>
                      <ExpandMore
                        expand={expandedCards[animal.id]}
                        onClick={() => handleExpandClick(animal.id)}
                        aria-expanded={expandedCards[animal.id]}
                        aria-label="show more"
                        sx={{ color: expandedCards[animal.id] ? 'inherit' : 'white' }}
                      >
                        <ExpandMoreIcon />
                      </ExpandMore>
                    </Box>

                    <Box sx={{ 
                      position: 'relative',
                      zIndex: 0,
                      mt: 1
                    }}>
                      <Collapse 
                        in={expandedCards[animal.id]} 
                        timeout={300} 
                        unmountOnExit
                        sx={{
                          transition: 'all 0.3s ease-in-out',
                          '&.MuiCollapse-entered': {
                            height: 'auto',
                            minHeight: '100%'
                          }
                        }}
                      >
                        <List dense disablePadding>
                          <ListItem disablePadding>
                            <ListItemIcon sx={{ minWidth: 36 }}>
                              <PetsIcon fontSize="small" />
                            </ListItemIcon>
                            <ListItemText 
                              primary="Type" 
                              secondary={animal.type}
                              primaryTypographyProps={{ 
                                variant: 'caption',
                                sx: { 
                                  fontFamily: '"Montserrat", sans-serif',
                                  fontWeight: 500,
                                  letterSpacing: '0.5px'
                                }
                              }}
                              secondaryTypographyProps={{ 
                                variant: 'body2',
                                sx: {
                                  letterSpacing: '0.3px'
                                }
                              }}
                            />
                          </ListItem>
                          <ListItem disablePadding>
                            <ListItemIcon sx={{ minWidth: 36 }}>
                              <SizeIcon fontSize="small" />
                            </ListItemIcon>
                            <ListItemText 
                              primary="Grootte" 
                              secondary={`${animal.size} cm (ongeveer)`}
                              primaryTypographyProps={{ variant: 'caption' }}
                              secondaryTypographyProps={{ variant: 'body2' }}
                            />
                          </ListItem>
                          <ListItem disablePadding>
                            <ListItemIcon sx={{ minWidth: 36 }}>
                              <ColorIcon fontSize="small" />
                            </ListItemIcon>
                            <ListItemText 
                              primary="Kleuren" 
                              secondary={animal.color.join(', ')}
                              primaryTypographyProps={{ variant: 'caption' }}
                              secondaryTypographyProps={{ variant: 'body2' }}
                            />
                          </ListItem>
                          <ListItem disablePadding>
                            <ListItemIcon sx={{ minWidth: 36 }}>
                              <InfoIcon fontSize="small" />
                            </ListItemIcon>
                            <ListItemText 
                              primary="Dieet" 
                              secondary={animal.diet}
                              primaryTypographyProps={{ variant: 'caption' }}
                              secondaryTypographyProps={{ variant: 'body2' }}
                            />
                          </ListItem>
                        </List>

                        <Box sx={{ mt: 1 }}>
                          <Typography 
                            variant="caption" 
                            gutterBottom
                            sx={{
                              fontFamily: '"Montserrat", sans-serif',
                              fontWeight: 500,
                              letterSpacing: '0.5px',
                              color: 'text.secondary',
                              display: 'block'
                            }}
                          >
                            Uiterlijke kenmerken:
                          </Typography>
                          <Typography 
                            variant="body2" 
                            paragraph 
                            sx={{ 
                              fontSize: '0.8rem',
                              letterSpacing: '0.3px',
                              lineHeight: 1.6
                            }}
                          >
                            {animal.description}
                          </Typography>
                        </Box>

                        <Box sx={{ mt: 1 }}>
                          <Typography 
                            variant="caption" 
                            gutterBottom
                            sx={{
                              fontFamily: '"Montserrat", sans-serif',
                              fontWeight: 500,
                              letterSpacing: '0.5px',
                              color: 'text.secondary',
                              display: 'block'
                            }}
                          >
                            Habitat:
                          </Typography>
                          <Typography 
                            variant="body2" 
                            sx={{ 
                              fontSize: '0.8rem',
                              letterSpacing: '0.3px'
                            }}
                          >
                            {animal.habitat}
                          </Typography>
                        </Box>
                      </Collapse>
                    </Box>
                  </CardContent>
                </StyledCard>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
