import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Card,
  CardContent,
  CardMedia,
  Chip
} from '@mui/material';
import animalsData from './data/animals.json';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#2e7d32',
    },
    secondary: {
      main: '#ff9800',
    },
  },
});

function App() {
  const [animals, setAnimals] = useState(animalsData.animals);
  const [filters, setFilters] = useState({
    type: '',
    size: '',
    color: '',
    habitat: '',
    diet: ''
  });

  const uniqueValues = (key) => {
    const values = new Set();
    animalsData.animals.forEach(animal => {
      if (Array.isArray(animal[key])) {
        animal[key].forEach(value => values.add(value));
      } else {
        values.add(animal[key]);
      }
    });
    return Array.from(values);
  };

  const handleFilterChange = (event) => {
    const { name, value } = event.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  useEffect(() => {
    const filteredAnimals = animalsData.animals.filter(animal => {
      return Object.entries(filters).every(([key, value]) => {
        if (!value) return true;
        if (Array.isArray(animal[key])) {
          return animal[key].includes(value);
        }
        return animal[key] === value;
      });
    });
    setAnimals(filteredAnimals);
  }, [filters]);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h3" component="h1" gutterBottom align="center">
            Madagaskar Wildlife Guide
          </Typography>

          <Paper sx={{ p: 3, mb: 4 }}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6} md={4}>
                <FormControl fullWidth>
                  <InputLabel>Type</InputLabel>
                  <Select
                    name="type"
                    value={filters.type}
                    onChange={handleFilterChange}
                  >
                    <MenuItem value="">Alle types</MenuItem>
                    {uniqueValues('type').map(type => (
                      <MenuItem key={type} value={type}>{type}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={6} md={4}>
                <FormControl fullWidth>
                  <InputLabel>Grootte</InputLabel>
                  <Select
                    name="size"
                    value={filters.size}
                    onChange={handleFilterChange}
                  >
                    <MenuItem value="">Alle groottes</MenuItem>
                    {uniqueValues('size').map(size => (
                      <MenuItem key={size} value={size}>{size}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={6} md={4}>
                <FormControl fullWidth>
                  <InputLabel>Kleur</InputLabel>
                  <Select
                    name="color"
                    value={filters.color}
                    onChange={handleFilterChange}
                  >
                    <MenuItem value="">Alle kleuren</MenuItem>
                    {uniqueValues('color').map(color => (
                      <MenuItem key={color} value={color}>{color}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
            </Grid>
          </Paper>

          <Grid container spacing={3}>
            {animals.map(animal => (
              <Grid item xs={12} sm={6} md={4} key={animal.id}>
                <Card>
                  <CardMedia
                    component="img"
                    height="200"
                    image={animal.image}
                    alt={animal.name}
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      {animal.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {animal.scientificName}
                    </Typography>
                    <Box sx={{ mt: 1 }}>
                      <Chip label={animal.type} size="small" sx={{ mr: 1 }} />
                      <Chip label={animal.size} size="small" sx={{ mr: 1 }} />
                      <Chip label={animal.habitat} size="small" />
                    </Box>
                    <Typography variant="body1" sx={{ mt: 2 }}>
                      {animal.description}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
