import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Stack,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon
} from '@mui/material';
import {
  Pets as PetsIcon,
  Straighten as SizeIcon,
  Palette as ColorIcon,
  Info as InfoIcon
} from '@mui/icons-material';
import animalsData from './data/animals.json';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#2e7d32',
    },
    secondary: {
      main: '#ff9800',
    },
  },
});

function App() {
  const [animals, setAnimals] = useState(animalsData.animals);
  const [filters, setFilters] = useState({
    type: [],
    size: [],
    color: []
  });

  const uniqueValues = (key) => {
    const values = new Set();
    animalsData.animals.forEach(animal => {
      if (Array.isArray(animal[key])) {
        animal[key].forEach(value => values.add(value));
      } else {
        values.add(animal[key]);
      }
    });
    return Array.from(values).sort();
  };

  const handleFilterClick = (category, value) => {
    setFilters(prev => {
      const currentValues = prev[category];
      const newValues = currentValues.includes(value)
        ? currentValues.filter(v => v !== value)
        : [...currentValues, value];

      return {
        ...prev,
        [category]: newValues
      };
    });
  };

  useEffect(() => {
    const filteredAnimals = animalsData.animals.filter(animal => {
      return Object.entries(filters).every(([key, values]) => {
        if (values.length === 0) return true;
        if (Array.isArray(animal[key])) {
          return values.some(value => animal[key].includes(value));
        }
        return values.includes(animal[key]);
      });
    });
    setAnimals(filteredAnimals);
  }, [filters]);

  const FilterSection = ({ title, category, values }) => (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle1" sx={{ mb: 1, fontWeight: 'bold' }}>
        {title}
      </Typography>
      <Stack direction="row" spacing={1} useFlexGap flexWrap="wrap">
        {values.map(value => (
          <Chip
            key={value}
            label={value}
            onClick={() => handleFilterClick(category, value)}
            color={filters[category].includes(value) ? "primary" : "default"}
            variant={filters[category].includes(value) ? "filled" : "outlined"}
            sx={{ mb: 1 }}
          />
        ))}
      </Stack>
    </Box>
  );

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h3" component="h1" gutterBottom align="center">
            Madagaskar Wildlife Guide
          </Typography>

          <Paper sx={{ p: 3, mb: 4 }}>
            <FilterSection
              title="Type"
              category="type"
              values={uniqueValues('type')}
            />
            <Divider sx={{ my: 2 }} />
            <FilterSection
              title="Grootte"
              category="size"
              values={uniqueValues('size')}
            />
            <Divider sx={{ my: 2 }} />
            <FilterSection
              title="Kleur"
              category="color"
              values={uniqueValues('color')}
            />
          </Paper>

          <Grid container spacing={3}>
            {animals.map(animal => (
              <Grid item xs={12} sm={6} md={4} key={animal.id}>
                <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                  <CardMedia
                    component="img"
                    height="200"
                    image={animal.image}
                    alt={animal.name}
                  />
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography gutterBottom variant="h5" component="div">
                      {animal.name}
                    </Typography>
                    <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                      {animal.scientificName}
                    </Typography>

                    <List dense>
                      <ListItem>
                        <ListItemIcon>
                          <PetsIcon />
                        </ListItemIcon>
                        <ListItemText primary="Type" secondary={animal.type} />
                      </ListItem>
                      <ListItem>
                        <ListItemIcon>
                          <SizeIcon />
                        </ListItemIcon>
                        <ListItemText primary="Grootte" secondary={animal.size} />
                      </ListItem>
                      <ListItem>
                        <ListItemIcon>
                          <ColorIcon />
                        </ListItemIcon>
                        <ListItemText 
                          primary="Kleuren" 
                          secondary={animal.color.join(', ')} 
                        />
                      </ListItem>
                      <ListItem>
                        <ListItemIcon>
                          <InfoIcon />
                        </ListItemIcon>
                        <ListItemText 
                          primary="Dieet" 
                          secondary={animal.diet} 
                        />
                      </ListItem>
                    </List>

                    <Box sx={{ mt: 2 }}>
                      <Typography variant="subtitle2" gutterBottom>
                        Uiterlijke kenmerken:
                      </Typography>
                      <Typography variant="body2" paragraph>
                        {animal.description}
                      </Typography>
                    </Box>

                    <Box sx={{ mt: 2 }}>
                      <Typography variant="subtitle2" gutterBottom>
                        Habitat:
                      </Typography>
                      <Typography variant="body2">
                        {animal.habitat}
                      </Typography>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
