import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Stack,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Slider,
  Button,
  IconButton,
  Tabs,
  Tab,
  useMediaQuery,
  SvgIcon
} from '@mui/material';
import {
  Pets as PetsIcon,
  Straighten as SizeIcon,
  Palette as ColorIcon,
  Info as InfoIcon,
  RestartAlt as ResetIcon,
  FilterList as FilterIcon
} from '@mui/icons-material';
import { styled } from '@mui/material/styles';
import animalsData from './data/animals.json';
import { alpha } from '@mui/material/styles';

// Custom Binoculars Icon
const BinocularsIcon = (props) => (
  <SvgIcon {...props}>
    <path d="M18 9.5V8c0-1.1-.9-2-2-2h-2c-1.1 0-2 .9-2 2v1h-2V8c0-1.1-.9-2-2-2H6c-1.1 0-2 .9-2 2v1.5c-1.1.3-2 1.3-2 2.5v3c0 1.1.9 2 2 2h3c1.1 0 2-.9 2-2v-3c0-1.2-.9-2.2-2-2.5V8c0-.3.2-.5.5-.5h2c.3 0 .5.2.5.5v4c0 1.1.9 2 2 2h2c1.1 0 2-.9 2-2V8c0-.3.2-.5.5-.5h2c.3 0 .5.2.5.5v1.5c-1.1.3-2 1.3-2 2.5v3c0 1.1.9 2 2 2h3c1.1 0 2-.9 2-2v-3c0-1.2-.9-2.2-2-2.5zM7 14.5c0 .3-.2.5-.5.5h-3c-.3 0-.5-.2-.5-.5v-3c0-.3.2-.5.5-.5h3c.3 0 .5.2.5.5v3zm12 0c0 .3-.2.5-.5.5h-3c-.3 0-.5-.2-.5-.5v-3c0-.3.2-.5.5-.5h3c.3 0 .5.2.5.5v3z" />
  </SvgIcon>
);

const StyledCard = styled(Card)(({ theme }) => ({
  position: 'relative',
  overflow: 'hidden',
  borderRadius: '16px',
  transition: 'all 0.3s ease-in-out',
  '&::before': {
    content: '""',
    position: 'absolute',
    inset: '-2px',
    borderRadius: '18px',
    padding: '2px',
    background: 'linear-gradient(45deg, transparent 40%, rgba(255, 215, 0, 0.5) 50%, transparent 60%)',
    WebkitMask: 'linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0)',
    WebkitMaskComposite: 'xor',
    maskComposite: 'exclude',
    opacity: 0,
    transition: 'all 0.3s ease-in-out',
    pointerEvents: 'none'
  },
  '&::after': {
    content: '""',
    position: 'absolute',
    inset: 0,
    borderRadius: '16px',
    background: `
      radial-gradient(circle at 50% 50%, rgba(255, 215, 0, 0.8) 0%, transparent 20%),
      radial-gradient(circle at 50% 50%, rgba(255, 223, 0, 0.6) 0%, transparent 30%),
      radial-gradient(circle at 50% 50%, rgba(255, 215, 0, 0.4) 0%, transparent 40%),
      radial-gradient(circle at 50% 50%, rgba(255, 223, 0, 0.2) 0%, transparent 55%)
    `,
    opacity: 0,
    mixBlendMode: 'color-dodge',
    transition: 'opacity 0.6s ease-in-out',
    pointerEvents: 'none',
    zIndex: 4
  },
  '&.spotted': {
    transform: 'translateY(-2px)',
    boxShadow: '0 4px 16px rgba(0,0,0,0.15)',
    '&::before': {
      opacity: 1,
      animation: 'shine 3s linear infinite'
    },
    '&::after': {
      opacity: 1,
      animation: 'glowPulse 4s ease-in-out infinite alternate'
    },
    '& .MuiCardMedia-root': {
      filter: 'none',
      '&::after': {
        opacity: 1,
        animation: 'glitter 6s ease-in-out infinite alternate'
      }
    }
  },
  '@keyframes shine': {
    '0%': {
      backgroundPosition: '200% 50%'
    },
    '100%': {
      backgroundPosition: '-200% 50%'
    }
  },
  '@keyframes glowPulse': {
    '0%': {
      opacity: 0.7,
      transform: 'scale(0.8)',
      filter: 'brightness(0.8) blur(3px)'
    },
    '50%': {
      opacity: 1,
      transform: 'scale(1.1)',
      filter: 'brightness(1.2) blur(2px)'
    },
    '100%': {
      opacity: 0.9,
      transform: 'scale(1)',
      filter: 'brightness(1) blur(2px)'
    }
  },
  '@keyframes glitter': {
    '0%': {
      background: `
        radial-gradient(circle at 20% 30%, rgba(255, 215, 0, 0.6) 0%, transparent 35%),
        radial-gradient(circle at 80% 20%, rgba(255, 223, 0, 0.6) 0%, transparent 30%),
        radial-gradient(circle at 40% 70%, rgba(255, 215, 0, 0.6) 0%, transparent 25%),
        radial-gradient(circle at 60% 50%, rgba(255, 223, 0, 0.6) 0%, transparent 40%),
        radial-gradient(circle at 90% 90%, rgba(255, 215, 0, 0.6) 0%, transparent 35%),
        radial-gradient(circle at 10% 90%, rgba(255, 223, 0, 0.6) 0%, transparent 30%),
        radial-gradient(circle at 70% 80%, rgba(255, 215, 0, 0.6) 0%, transparent 33%),
        radial-gradient(circle at 30% 40%, rgba(255, 215, 0, 0.6) 0%, transparent 28%),
        radial-gradient(circle at 50% 95%, rgba(255, 223, 0, 0.6) 0%, transparent 32%),
        radial-gradient(circle at 85% 40%, rgba(255, 215, 0, 0.6) 0%, transparent 38%)
      `,
      opacity: 0.7,
      transform: 'scale(1) rotate(0deg)',
      filter: 'brightness(1)'
    },
    '25%': {
      background: `
        radial-gradient(circle at 25% 25%, rgba(255, 215, 0, 0.6) 0%, transparent 35%),
        radial-gradient(circle at 85% 25%, rgba(255, 223, 0, 0.6) 0%, transparent 30%),
        radial-gradient(circle at 45% 75%, rgba(255, 215, 0, 0.6) 0%, transparent 25%),
        radial-gradient(circle at 65% 45%, rgba(255, 223, 0, 0.6) 0%, transparent 40%),
        radial-gradient(circle at 95% 85%, rgba(255, 215, 0, 0.6) 0%, transparent 35%),
        radial-gradient(circle at 15% 85%, rgba(255, 223, 0, 0.6) 0%, transparent 30%),
        radial-gradient(circle at 75% 75%, rgba(255, 215, 0, 0.6) 0%, transparent 33%),
        radial-gradient(circle at 35% 35%, rgba(255, 215, 0, 0.6) 0%, transparent 28%),
        radial-gradient(circle at 55% 90%, rgba(255, 223, 0, 0.6) 0%, transparent 32%),
        radial-gradient(circle at 90% 35%, rgba(255, 215, 0, 0.6) 0%, transparent 38%)
      `,
      opacity: 0.9,
      transform: 'scale(1.1) rotate(2deg)',
      filter: 'brightness(1.2)'
    },
    '50%': {
      background: `
        radial-gradient(circle at 30% 20%, rgba(255, 215, 0, 0.6) 0%, transparent 35%),
        radial-gradient(circle at 90% 30%, rgba(255, 223, 0, 0.6) 0%, transparent 30%),
        radial-gradient(circle at 50% 80%, rgba(255, 215, 0, 0.6) 0%, transparent 25%),
        radial-gradient(circle at 70% 40%, rgba(255, 223, 0, 0.6) 0%, transparent 40%),
        radial-gradient(circle at 85% 95%, rgba(255, 215, 0, 0.6) 0%, transparent 35%),
        radial-gradient(circle at 5% 95%, rgba(255, 223, 0, 0.6) 0%, transparent 30%),
        radial-gradient(circle at 65% 85%, rgba(255, 215, 0, 0.6) 0%, transparent 33%),
        radial-gradient(circle at 25% 45%, rgba(255, 215, 0, 0.6) 0%, transparent 28%),
        radial-gradient(circle at 45% 100%, rgba(255, 223, 0, 0.6) 0%, transparent 32%),
        radial-gradient(circle at 95% 45%, rgba(255, 215, 0, 0.6) 0%, transparent 38%)
      `,
      opacity: 0.5,
      transform: 'scale(0.95) rotate(-1deg)',
      filter: 'brightness(0.9)'
    },
    '75%': {
      background: `
        radial-gradient(circle at 15% 35%, rgba(255, 215, 0, 0.6) 0%, transparent 35%),
        radial-gradient(circle at 75% 15%, rgba(255, 223, 0, 0.6) 0%, transparent 30%),
        radial-gradient(circle at 35% 65%, rgba(255, 215, 0, 0.6) 0%, transparent 25%),
        radial-gradient(circle at 55% 55%, rgba(255, 223, 0, 0.6) 0%, transparent 40%),
        radial-gradient(circle at 95% 95%, rgba(255, 215, 0, 0.6) 0%, transparent 35%),
        radial-gradient(circle at 5% 85%, rgba(255, 223, 0, 0.6) 0%, transparent 30%),
        radial-gradient(circle at 65% 75%, rgba(255, 215, 0, 0.6) 0%, transparent 33%),
        radial-gradient(circle at 25% 35%, rgba(255, 215, 0, 0.6) 0%, transparent 28%),
        radial-gradient(circle at 45% 90%, rgba(255, 223, 0, 0.6) 0%, transparent 32%),
        radial-gradient(circle at 80% 45%, rgba(255, 215, 0, 0.6) 0%, transparent 38%)
      `,
      opacity: 0.8,
      transform: 'scale(1.05) rotate(1deg)',
      filter: 'brightness(1.1)'
    },
    '100%': {
      background: `
        radial-gradient(circle at 20% 30%, rgba(255, 215, 0, 0.6) 0%, transparent 35%),
        radial-gradient(circle at 80% 20%, rgba(255, 223, 0, 0.6) 0%, transparent 30%),
        radial-gradient(circle at 40% 70%, rgba(255, 215, 0, 0.6) 0%, transparent 25%),
        radial-gradient(circle at 60% 50%, rgba(255, 223, 0, 0.6) 0%, transparent 40%),
        radial-gradient(circle at 90% 90%, rgba(255, 215, 0, 0.6) 0%, transparent 35%),
        radial-gradient(circle at 10% 90%, rgba(255, 223, 0, 0.6) 0%, transparent 30%),
        radial-gradient(circle at 70% 80%, rgba(255, 215, 0, 0.6) 0%, transparent 33%),
        radial-gradient(circle at 30% 40%, rgba(255, 215, 0, 0.6) 0%, transparent 28%),
        radial-gradient(circle at 50% 95%, rgba(255, 223, 0, 0.6) 0%, transparent 32%),
        radial-gradient(circle at 85% 40%, rgba(255, 215, 0, 0.6) 0%, transparent 38%)
      `,
      opacity: 0.7,
      transform: 'scale(1) rotate(0deg)',
      filter: 'brightness(1)'
    }
  },
  '& .MuiCardMedia-root': {
    height: 0,
    paddingTop: '100%',
    transition: 'all 0.3s ease-in-out',
    position: 'relative',
    '&::after': {
      content: '""',
      position: 'absolute',
      inset: 0,
      background: 'linear-gradient(45deg, rgba(255,223,0,0.2), rgba(255,215,0,0.1), rgba(255,215,0,0.2))',
      opacity: 0,
      transition: 'opacity 0.3s ease-in-out',
      pointerEvents: 'none'
    }
  },
  '& .MuiCardContent-root': {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    background: 'linear-gradient(to top, rgba(0,0,0,0.75) 0%, rgba(0,0,0,0.2) 50%, rgba(0,0,0,0) 100%)',
    color: 'white',
    padding: 0,
    transition: 'all 0.3s ease-in-out',
    transform: 'translateY(0)',
    '&:last-child': {
      paddingBottom: 0
    }
  }
}));

const InfoLabel = styled(Box)(({ theme }) => ({
  position: 'relative',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  color: 'white',
  padding: '2px 8px',
  borderRadius: '12px',
  fontSize: '0.675rem',
  display: 'inline-flex',
  alignItems: 'center',
  gap: '4px',
  backdropFilter: 'blur(8px)',
  zIndex: 2,
  width: 'auto',
  alignSelf: 'flex-end',
  letterSpacing: '0.3px',
  border: '1px solid rgba(255, 255, 255, 0.1)',
  '& .MuiSvgIcon-root': {
    fontSize: '0.75rem'
  }
}));

const FilterContainer = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  marginBottom: theme.spacing(4),
  borderRadius: theme.spacing(2),
  '& .MuiDivider-root': {
    margin: theme.spacing(3, 0)
  }
}));

const FilterSection = ({ icon: Icon, category, values, selectedValues, onValueClick }) => (
  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
    <Icon 
      sx={{ 
        fontSize: 20,
        color: 'grey.500',
        width: 20
      }}
    />
    <Stack 
      direction="row" 
      spacing={0.75} 
      useFlexGap 
      flexWrap="wrap"
      sx={{
        '& .MuiChip-root': {
          borderRadius: '6px',
          height: '24px',
          fontSize: '0.8125rem',
          fontWeight: 500,
          transition: 'all 0.2s ease-in-out',
          '&:hover': {
            transform: 'translateY(-1px)',
            boxShadow: '0 2px 4px rgba(0,0,0,0.06)'
          },
          '&.MuiChip-outlined': {
            borderColor: 'grey.300',
            color: 'grey.700',
            backgroundColor: 'white'
          }
        }
      }}
    >
      {values.map(value => (
        <Chip
          key={value}
          label={value}
          onClick={() => onValueClick(category, value)}
          color="primary"
          variant={selectedValues.includes(value) ? "filled" : "outlined"}
          size="small"
        />
      ))}
    </Stack>
  </Box>
);

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#2e7d32',
    },
    secondary: {
      main: '#ff9800',
    },
  },
  typography: {
    fontFamily: '"Open Sans", sans-serif',
    h1: {
      fontFamily: '"Righteous", cursive',
      fontWeight: 600,
    },
    h2: {
      fontFamily: '"Righteous", cursive',
      fontWeight: 600,
    },
    h3: {
      fontFamily: '"Righteous", cursive',
      fontWeight: 600,
    },
    h4: {
      fontFamily: '"Righteous", cursive',
      fontWeight: 600,
    },
    h5: {
      fontFamily: '"Righteous", cursive',
      fontWeight: 600,
    },
    h6: {
      fontFamily: '"Righteous", cursive',
      fontWeight: 600,
    },
    subtitle1: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
    },
    subtitle2: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
    },
    body1: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 400,
    },
    body2: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 400,
    },
    caption: {
      fontFamily: '"Open Sans", sans-serif',
      fontWeight: 300,
    },
    button: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 500,
      textTransform: 'none',
    },
  },
});

function App() {
  const minSize = Math.floor(Math.min(...animalsData.animals.map(animal => animal.size)) * 0.8);
  const maxSize = Math.ceil(Math.max(...animalsData.animals.map(animal => animal.size)) * 1.2);

  const [animals, setAnimals] = useState(animalsData.animals);
  const [filters, setFilters] = useState({
    type: [],
    color: []
  });
  const [sizeRange, setSizeRange] = useState([minSize, maxSize]);
  const [spottedAnimals, setSpottedAnimals] = useState(() => {
    const saved = localStorage.getItem('spottedAnimals');
    return saved ? JSON.parse(saved) : {};
  });
  const [activeTab, setActiveTab] = useState(0);
  const [isFilterExpanded, setIsFilterExpanded] = useState(false);
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  useEffect(() => {
    localStorage.setItem('spottedAnimals', JSON.stringify(spottedAnimals));
  }, [spottedAnimals]);

  const handleSpottedToggle = (id) => {
    setSpottedAnimals(prev => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  const uniqueValues = (key) => {
    const values = new Set();
    animalsData.animals.forEach(animal => {
      if (Array.isArray(animal[key])) {
        animal[key].forEach(value => values.add(value));
      } else {
        values.add(animal[key]);
      }
    });
    return Array.from(values).sort();
  };

  const handleFilterClick = (category, value) => {
    setFilters(prev => {
      const currentValues = prev[category];
      const newValues = currentValues.includes(value)
        ? currentValues.filter(v => v !== value)
        : [...currentValues, value];

      return {
        ...prev,
        [category]: newValues
      };
    });
  };

  const handleSizeChange = (event, newValue) => {
    setSizeRange(newValue);
  };

  useEffect(() => {
    const filteredAnimals = animalsData.animals.filter(animal => {
      if (activeTab === 1 && !spottedAnimals[animal.id]) {
        return false;
      }

      const typeColorMatch = Object.entries(filters).every(([key, values]) => {
        if (values.length === 0) return true;
        if (Array.isArray(animal[key])) {
          return values.some(value => animal[key].includes(value));
        }
        return values.includes(animal[key]);
      });

      const sizeMargin = animal.size * 0.2;
      const minSizeWithMargin = animal.size - sizeMargin;
      const maxSizeWithMargin = animal.size + sizeMargin;

      const sizeMatch = 
        (minSizeWithMargin <= sizeRange[1]) && 
        (maxSizeWithMargin >= sizeRange[0]);

      return typeColorMatch && sizeMatch;
    });
    setAnimals(filteredAnimals);
  }, [filters, sizeRange, activeTab, spottedAnimals]);

  const handleImageError = (e) => {
    e.target.src = '/images/placeholder.jpg';
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const resetFilters = () => {
    setFilters({
      type: [],
      color: []
    });
    setSizeRange([minSize, maxSize]);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container 
        maxWidth="lg" 
        sx={{
          px: { xs: 0, sm: 3 }
        }}
      >
        <Box sx={{ 
          mt: 0,
          mb: 4
        }}>
          <Box 
            sx={{ 
              display: 'flex', 
              alignItems: 'flex-start',
              gap: 3,
              mb: 2,
              px: { xs: 2, sm: 4 },
              pb: 3,
              pt: 2,
              background: 'linear-gradient(180deg, rgba(46,125,50,0.12) 0%, rgba(46,125,50,0) 100%)',
              borderRadius: { xs: 0, sm: '16px' }
            }}
          >
            <img 
              src="/images/pocketspot-logo.png" 
              alt="PocketSpot Logo" 
              style={{
                height: '120px',
                width: 'auto',
                marginTop: '12px'
              }}
            />
            <Box sx={{ mt: 2 }}>
              <Typography 
                variant="h3" 
                component="h1" 
                sx={{ 
                  fontFamily: '"Righteous", cursive',
                  color: 'primary.main',
                  mb: 0.5,
                  letterSpacing: '0.5px',
                  fontSize: { xs: '2rem', sm: '2.5rem' }
                }}
              >
                Pocket Spot
              </Typography>
              <Typography 
                variant="subtitle1" 
                sx={{ 
                  fontFamily: '"Playfair Display", serif',
                  fontStyle: 'italic',
                  color: 'text.secondary',
                  letterSpacing: '0.5px',
                  fontSize: { xs: '0.875rem', sm: '1rem' }
                }}
              >
                by Benjamin
              </Typography>
            </Box>
          </Box>

          <Box sx={{ px: { xs: 2, sm: 0 } }}>
            <Box sx={{ 
              borderBottom: 1, 
              borderColor: 'divider', 
              mb: 2.5
            }}>
              <Tabs 
                value={activeTab} 
                onChange={handleTabChange} 
                variant="fullWidth"
                textColor="primary"
                indicatorColor="primary"
              >
                <Tab 
                  label={
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <span>Alle dieren</span>
                      <Chip 
                        label={animalsData.animals.length} 
                        size="small" 
                        color={activeTab === 0 ? "primary" : "default"}
                      />
                    </Box>
                  } 
                />
                <Tab 
                  label={
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <span>Gespot</span>
                      <Chip 
                        label={Object.values(spottedAnimals).filter(Boolean).length} 
                        size="small"
                        color={activeTab === 1 ? "primary" : "default"}
                      />
                    </Box>
                  } 
                />
              </Tabs>
            </Box>

            <Paper 
              sx={{ 
                mb: 3,
                p: 2,
                backgroundColor: alpha(theme.palette.primary.main, 0.06),
                borderRadius: 2,
                border: '1px solid',
                borderColor: alpha(theme.palette.primary.main, 0.12)
              }}
              elevation={0}
            >
              <Stack spacing={2}>
                <FilterSection
                  icon={PetsIcon}
                  category="type"
                  values={uniqueValues('type')}
                  selectedValues={filters.type}
                  onValueClick={handleFilterClick}
                />

                <FilterSection
                  icon={ColorIcon}
                  category="color"
                  values={uniqueValues('color')}
                  selectedValues={filters.color}
                  onValueClick={handleFilterClick}
                />

                <Box>
                  <Box sx={{ 
                    display: 'flex', 
                    alignItems: 'center',
                    gap: 1.5
                  }}>
                    <SizeIcon 
                      sx={{ 
                        fontSize: 20,
                        color: 'grey.500',
                        width: 20
                      }}
                    />
                    <Box sx={{ 
                      display: 'flex', 
                      alignItems: 'center', 
                      gap: 1,
                      flexGrow: 1,
                      backgroundColor: 'white',
                      borderRadius: 1.5,
                      py: 0.75,
                      px: 1,
                      border: '1px solid',
                      borderColor: 'grey.200'
                    }}>
                      <Typography 
                        variant="caption" 
                        sx={{ 
                          color: 'grey.600',
                          fontWeight: 500,
                          minWidth: '20px',
                          textAlign: 'right'
                        }}
                      >
                        {sizeRange[0]}
                      </Typography>
                      <Slider
                        value={sizeRange}
                        onChange={handleSizeChange}
                        valueLabelDisplay="auto"
                        min={minSize}
                        max={maxSize}
                        size="small"
                        sx={{ 
                          mx: 0.5,
                          '& .MuiSlider-rail': {
                            opacity: 0.32
                          },
                          '& .MuiSlider-track': {
                            border: 'none'
                          },
                          '& .MuiSlider-thumb': {
                            width: 12,
                            height: 12,
                            backgroundColor: 'white',
                            border: '2px solid',
                            borderColor: 'primary.main',
                            boxShadow: '0 1px 2px rgba(0,0,0,0.1)',
                            '&:hover, &.Mui-focusVisible': {
                              boxShadow: `0 0 0 6px ${alpha(theme.palette.primary.main, 0.16)}`
                            },
                            '&.Mui-active': {
                              boxShadow: `0 0 0 8px ${alpha(theme.palette.primary.main, 0.16)}`
                            }
                          },
                          '& .MuiSlider-valueLabel': {
                            backgroundColor: 'grey.800'
                          }
                        }}
                      />
                      <Typography 
                        variant="caption" 
                        sx={{ 
                          color: 'grey.600',
                          fontWeight: 500,
                          minWidth: '20px'
                        }}
                      >
                        {sizeRange[1]}
                      </Typography>
                    </Box>
                  </Box>
                </Box>

                <Box sx={{ 
                  display: 'flex', 
                  justifyContent: 'center'
                }}>
                  <Button
                    variant="outlined"
                    startIcon={<ResetIcon />}
                    onClick={resetFilters}
                    size="small"
                    sx={{
                      borderRadius: '6px',
                      textTransform: 'none',
                      borderColor: 'grey.300',
                      color: 'grey.700',
                      backgroundColor: 'white',
                      fontSize: '0.8125rem',
                      fontWeight: 500,
                      px: 2,
                      '&:hover': {
                        backgroundColor: 'white',
                        borderColor: 'grey.400',
                        transform: 'translateY(-1px)',
                        boxShadow: '0 2px 4px rgba(0,0,0,0.06)'
                      }
                    }}
                  >
                    Reset filters
                  </Button>
                </Box>
              </Stack>
            </Paper>

            {animals.length === 0 ? (
              <Box sx={{ textAlign: 'center', py: 4 }}>
                <Typography variant="h6" color="text.secondary">
                  {activeTab === 1 ? 
                    "Je hebt nog geen dieren gespot die aan de filters voldoen." :
                    "Geen dieren gevonden die aan de filters voldoen."
                  }
                </Typography>
              </Box>
            ) : (
              <Grid container spacing={2}>
                {animals.map(animal => (
                  <Grid item xs={6} sm={4} md={3} key={animal.id}>
                    <StyledCard className={spottedAnimals[animal.id] ? 'spotted' : ''}>
                      <CardMedia
                        component="img"
                        image={animal.image}
                        alt={animal.name}
                        onError={handleImageError}
                        sx={{
                          objectFit: 'cover',
                          backgroundColor: 'grey.100',
                          filter: spottedAnimals[animal.id] ? 'none' : 'grayscale(0.5) brightness(0.95)'
                        }}
                      />
                      <Box sx={{ 
                        position: 'absolute', 
                        top: theme.spacing(1), 
                        left: theme.spacing(1),
                        zIndex: 3
                      }}>
                        <IconButton
                          size="small"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleSpottedToggle(animal.id);
                          }}
                          sx={{ 
                            color: 'white',
                            backgroundColor: spottedAnimals[animal.id] ? 'rgba(255, 215, 0, 0.85)' : 'rgba(0, 0, 0, 0.5)',
                            backdropFilter: 'blur(8px)',
                            transition: 'all 0.3s ease-in-out',
                            padding: '6px',
                            border: '1px solid',
                            borderColor: spottedAnimals[animal.id] ? 'rgba(255, 215, 0, 0.5)' : 'rgba(255, 255, 255, 0.1)',
                            '&:hover': {
                              backgroundColor: spottedAnimals[animal.id] ? 'rgba(255, 215, 0, 0.95)' : 'rgba(0, 0, 0, 0.6)',
                              transform: 'scale(1.05)'
                            }
                          }}
                        >
                          <BinocularsIcon 
                            sx={{
                              fontSize: '1.25rem'
                            }}
                          />
                        </IconButton>
                      </Box>
                      <Box sx={{ 
                        position: 'absolute', 
                        top: theme.spacing(1), 
                        right: theme.spacing(1),
                        display: 'flex',
                        flexDirection: 'column',
                        gap: 0.5,
                        zIndex: 3,
                        alignItems: 'flex-end'
                      }}>
                        <InfoLabel>
                          <PetsIcon />
                          {animal.type}
                        </InfoLabel>
                        <InfoLabel>
                          <SizeIcon />
                          {animal.size} cm
                        </InfoLabel>
                      </Box>
                      <CardContent>
                        <Box sx={{ 
                          display: 'flex', 
                          flexDirection: 'column',
                          position: 'relative',
                          minHeight: '100%',
                          px: 1.5,
                          pb: 1.5
                        }}>
                          <Box sx={{ 
                            display: 'flex', 
                            alignItems: 'flex-end',
                            width: '100%',
                            position: 'relative',
                            zIndex: 1,
                            mt: 'auto'
                          }}>
                            <Box sx={{ flexGrow: 1 }}>
                              <Typography 
                                variant="h6" 
                                component="div" 
                                noWrap
                                sx={{
                                  fontWeight: 600,
                                  letterSpacing: '0.75px',
                                  fontSize: '0.9rem',
                                  mb: 0.25,
                                  lineHeight: 1.2,
                                  textShadow: '0 1px 2px rgba(0,0,0,0.3)'
                                }}
                              >
                                {animal.name}
                              </Typography>
                              <Typography 
                                variant="body2" 
                                noWrap
                                sx={{
                                  fontFamily: '"Playfair Display", serif',
                                  fontStyle: 'italic',
                                  fontSize: '0.75rem',
                                  color: 'rgba(255, 255, 255, 0.85)',
                                  letterSpacing: '0.5px',
                                  lineHeight: 1.2,
                                  textShadow: '0 2px 4px rgba(0,0,0,0.4)',
                                  fontWeight: 300
                                }}
                              >
                                {animal.scientificName}
                              </Typography>
                            </Box>
                          </Box>
                        </Box>
                      </CardContent>
                    </StyledCard>
                  </Grid>
                ))}
              </Grid>
            )}
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
